import { createSlice } from "@reduxjs/toolkit"


const initialState = {
    search:'',
    region:'',
}

export const controlsSlice = createSlice({
        name: 'controls',
        initialState,
        reducers:{
            setSearch:(state,{payload})=>state.search=payload,
            setSelect:(state,{payload})=>state.region=payload,
            clearControls:()=>initialState,

        }
}); 

export const {setSearch,setSelect,clearControls}= controlsSlice.actions;
export const controlsReducer = controlsSlice.reducer;

export const selectSearch = (state)=>{
    return state.control.search
}

export const selectSelect = (state)=>{
    return state.control.region
}
export const selectControls = (state)=>{
    return state.control
}
