export const SET_SEARCH = '@@controls/SET_SEARCH';
export const SET_SELECT = '@@controls/SET_SELECT';

export  const setSearch=(search)=>({
        type:SET_SEARCH,
        payload: search,
})
export  const setSelect=(select)=>({
        type:SET_SELECT,
        payload: select,
})
