import styled from 'styled-components';
import { useDispatch, useSelector } from 'react-redux';
import { setSelect } from '../store/controls/controlsSlice';

import { Search } from './Search';
import { CustomSelect } from './CustomSelect';
import { selectSelect } from '../store/controls/controlsSlice';

const optionsMap = {
  'Africa': { value: 'Africa', label: 'Africa' },
  'America': { value: 'America', label: 'America' },
  'Asia': { value: 'Asia', label: 'Asia' },
  'Europe': { value: 'Europe', label: 'Europe' },
  'Oceania': { value: 'Oceania', label: 'Oceania' },
}
const options = Object.values(optionsMap);

const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;

  @media (min-width: 767px) {
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
  }
`;

export const Controls = () => {

  const dispatch = useDispatch();
  const handlSelect = (reg) => {
    dispatch(setSelect(reg?.value || ''))
  }
  const region = useSelector(selectSelect);
  return (
    <Wrapper>
      <Search />
      <CustomSelect
        options={options}
        placeholder="Filter by Region"
        isClearable
        isSearchable={false}
        value={optionsMap[region]}
        onChange={handlSelect}
      />
    </Wrapper>
  );
};
