import { SET_COUNTRIES, SET_LOADING, SET_ERROR } from "./contries.action";
const initialState={
    status: 'idle',
    error:null,
    list:[]
}

export const countriesReducer = (state=initialState, action)=>{
    switch (action.type) {
        case SET_COUNTRIES:
            return{
                ...state,
                status:'received',
                list:action.payload,
            }
        case SET_LOADING:
            return{
                ...state,
                status:'loading',
                error:null,

            }
        case SET_ERROR:
            return{
                ...state,
                status:'rejected',
                error:action.payload,
            }


        default:
           return state;
    }
}
