import { SET_COUNTRY,SET_LOADING, SET_ERROR } from "./details-actions";

const initialState = {
   currentCountry: null,
   status:'idle',
   error: null, 
}

export const detailsReducer = (state = initialState, {type, payload})=>{
    switch(type){
        case SET_COUNTRY:
            return{
                ...state,
                currentCountry:payload,
                status:'received',
            }
        case SET_LOADING:
            return{

                currentCountry:null,
                status:'loading',
                error:null,
            }
        case SET_ERROR:
            return{
                ...state,
                status:'rejected',
                error:payload,
            }
        default:
            return state;
    }
}
