import { useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { useEffect } from 'react';
import { List } from '../components/List';
import { Card } from '../components/Card';
import { Controls } from '../components/Controls';
import { selectCountriesInfo, selectVisiableCountries } from '../store/contries/contries.selector';
import { loadCountries } from '../store/contries/contries.action';
import { selectControls } from '../store/controls/controls-selector';


export const HomePage = () => {

  const navigate = useNavigate();

  const { search, region } = useSelector(selectControls);

  const countries = useSelector(state => selectVisiableCountries(state, { search, region }));

  const { status, error, qty } = useSelector(selectCountriesInfo)
  const dispatch = useDispatch()

  useEffect(() => {
    if (!qty) {
      dispatch(loadCountries())
    }
  }, [qty, dispatch])

  return (
    <>
