import { SET_SEARCH, SET_SELECT, CLEAR_CONTROLS} from "./controls-action";

const initialState = {
    search:'',
    region:'',
}

export const controlReducer =(state=initialState, {type,payload})=>{
    switch (type) {
        case SET_SEARCH:      
        return {
            ...state,
            search:payload,
        }
        case SET_SELECT:      
        return {
            ...state,
            region:payload,
        }

        case CLEAR_CONTROLS:
            return{
                search:'',
                region:'', 
            }

        default:
            return state;
    }
}
